import * as React from "react";
import { Text, View, StyleSheet, TouchableOpacity } from "react-native";
import { Header, Icon } from "react-native-elements";
import CheckBoxScreen from "../components/CheckBox";
import db from "../config";
import firebase from "firebase";

export default class TestScreen extends React.Component {
  constructor() {
    super();
    this.state = {
      questionNo: 1,
      finalPoints: 0,
      PerQuestion: 0,
      docId: "",
    };
  }

  getUserData = async () => {
    var user = firebase.auth().currentUser;
    var email = user.email;

    await db
      .collection("Users")
      .where("email", "==", email)
      .get()
      .then((snapshot) => {
        snapshot.forEach((doc) => {
          this.setState({
            docId: doc.id,
            PerQuestion: doc.data().PerQuestion,
          });
        });
      });
    console.log("PerQuestionFirebase: " + this.state.PerQuestion);
    console.log("finalPointsFirebase: " + this.state.finalPoints);
    console.log("DocId: " + this.state.docId);
  };

  FinalUpdate = async () => {
    var user = firebase.auth().currentUser;
    var email = user.email;
    await db
      .collection("Users")
      .where("email", "==", email)
      .get()
      .then((snapshot) => {
        snapshot.forEach((doc) => {
          this.setState({
            finalPoints: doc.data().FINALSCORE,
          });
        });
      });

    var final = this.state.finalPoints;
    var perQues = this.state.PerQuestion;
    var finalScore = final + perQues;

    console.log("final: " + final);
    console.log("PerQuestion: " + perQues);
    console.log("finalScore: " + finalScore);
    console.log("  ");

    await db.collection("Users").doc(this.state.docId).update({
      FINALSCORE: finalScore,
    });
  };

  componentDidMount() {
    this.getUserData();
  }

  render() {
    // Question 1
    if (this.state.questionNo === 1) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="When i'm around people, I still struggle feeling alone"
              answere1="Not Really, no"
              answere2="Sometimes"
              answere3="All The Time"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.getUserData();
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //question 2
    else if (this.state.questionNo === 2) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="I feel alone..."
              answere1="Not Really, no"
              answere3="All The Time"
              answere2="Sometimes"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.getUserData();
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //Question 3
    else if (this.state.questionNo === 3) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="I have put my friendships on the back burner."
              answere1="No, not really"
              answere2="A bit"
              answere3="Yes, clearly"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.getUserData();
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //question 4
    else if (this.state.questionNo === 4) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="My family just doesn't have the same priority in my life as they use to."
              answere1="I disagree. My family has remained important"
              answere2="A bit"
              answere3="This is Truth"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.getUserData();
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //Question 5
    else if (this.state.questionNo === 5) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="People exhaust me"
              answere1="Not really, no"
              answere2="Sometimes"
              answere3="Absolutely agee"
