import * as React from "react";
import { Text, View, StyleSheet, TouchableOpacity } from "react-native";
import { Header, Icon } from "react-native-elements";
import CheckBoxScreen from "../components/CheckBox";
import db from "../config";
import firebase from "firebase";

export default class TestScreen extends React.Component {
  constructor() {
    super();
    this.state = {
      questionNo: 1,
      finalPoints: 0,
      PerQuestion: 0,
      docId: "",
    };
  }

  getUserData = () => {
    var user = firebase.auth().currentUser;
    var email = user.email;

    db.collection("Users")
      .where("email", "==", email)
      .get()
      .then((snapshot) => {
        snapshot.forEach((doc) => {
          this.setState({
            docId: doc.id,
            PerQuestion: doc.data().PerQuestion,
          });
        });
      });
  };
  FinalUpdate = () => {
    var final = this.state.finalPoints;
    var perQues = this.state.PerQuestion;
    var finalScore = final + perQues;

    console.log("final: " + finalScore);

    db.collection("Users").doc(this.state.docId).update({
      finalPoints: finalScore,
    });
  };

  componentDidMount() {
    this.getUserData();
  }

  render() {
    console.log(this.state.finalPoints);
    // Question 1
    if (this.state.questionNo === 1) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="When i'm around people, I still struggle feeling alone"
              answere1="All The Time"
              answere2="Sometimes"
              answere3="Not Really, no"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.getUserData()
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //question 2
    else if (this.state.questionNo === 2) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="I feel alone..."
              answere1="All The Time"
              answere2="Sometimes"
              answere3="Not Really, no"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //Question 3
    else if (this.state.questionNo === 3) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="I have put my friendships on the back burner."
              answere1="Yes, clearly"
              answere2="A bit"
              answere3="No, not really"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //question 4
    else if (this.state.questionNo === 4) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="My family just doesn't have the same priority in my life as they use to."
              answere1="This is Truth"
              answere2="A bit"
              answere3="I disagree. My family has remained important"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //Question 5
    else if (this.state.questionNo === 5) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="People exhaust me"
              answere1="Absolutely agee"
              answere2="Sometimes"
              answere3="Not really, no"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //Question 6
    else if (this.state.questionNo === 6) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="My appetite has changed and..."
              answere1="Not really"
              answere2="I've lost some weight"
              answere3="I've gained a little weight"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //Question 7
    else if (this.state.questionNo === 7) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="I have given up doing things that I use to be excited about (Sports, Hobbies etc.)"
              answere1="Yes, I would say so."
              answere2="A little. Some things."
              answere3="Not Really, no"
            />
          </View>

          <TouchableOpacity
            style={[styles.button, { marginLeft: "25%" }]}
            onPress={() => {
              const questionNo = this.state.questionNo;
              this.setState({
                questionNo: questionNo + 1,
              });
              this.FinalUpdate();
            }}
          >
            <Text style={{ color: "black", fontWeight: "bold", marginTop: 5 }}>
              Next
            </Text>
          </TouchableOpacity>
        </View>
      );
    }

    //Question 8
    else if (this.state.questionNo === 8) {
      return (
        <View>
          <Header
            leftComponent={
              <Icon
                name="arrow-left"
                type="feather"
                color="white"
                onPress={() => {
                  this.props.navigation.navigate("Drawer");
                }}
              />
            }
            centerComponent={{
              text: "Test",
              style: { color: "yellow", fontSize: 20, fontWeight: "bold" },
            }}
            backgroundColor="black"
          />
          <View>
            <CheckBoxScreen
              question="My tastes in audio/visual has turned 'dark.'"
